INSERT INTO `pages` VALUES(6, 0, 0, 'Tuyển dụng', 'tuyen-dung', '<p><strong>Hi</strong><strong>ệ</strong><strong>n t</strong><strong>ạ</strong><strong>i, c&ocirc;ng ty TNHH &ocirc; t&ocirc; ISUZU Vi</strong><strong>ệ</strong><strong>t Nam đang c&oacute; nhu c</strong><strong>ầ</strong><strong>u tuy</strong><strong>ể</strong><strong>n d</strong><strong>ụ</strong><strong>ng c&aacute;c v</strong><strong>ị</strong><strong> tr&iacute; sau:</strong></p>\n<p>&diams;&nbsp; C&aacute;c y&ecirc;u cầu chung:</p>\n<p>- &nbsp; Tuổi: dưới 30 tuổi.</p>\n<p>- &nbsp; Tr&igrave;nh độ: Tốt nghiệp hệ Đại học c&aacute;c ng&agrave;nh: Kinh tế, Kế to&aacute;n, Marketing, Luật, Kỹ thuật.</p>\n<p>- &nbsp; Kỹ năng vi t&iacute;nh: th&agrave;nh thạo vi t&iacute;nh văn ph&ograve;ng (Microsoft Office).</p>\n<p><strong>Nh&acirc;n vi&ecirc;n phi&ecirc;n d</strong><strong>ị</strong><strong>ch: (S</strong><strong>ố</strong><strong> l</strong><strong>ượ</strong><strong>ng: 02 ng</strong><strong>ườ</strong><strong>i</strong><strong>)</strong></p>\n<p>- &nbsp; Th&ocirc;ng thạo cả 2 ngoại ngữ:</p>\n<p>+ English: TOEIC &gt; 800.</p>\n<p>+ Japanese:&nbsp; tr&igrave;nh độ cao cấp ( ~ 2 Kyu).</p>\n<p><strong><em>*</em></strong><strong><em>Ư</em></strong><strong><em>u ti&ecirc;n c&aacute;c </em></strong><strong><em>ứ</em></strong><strong><em>ng vi&ecirc;n t</em></strong><strong><em>ố</em></strong><strong><em>t nghi</em></strong><strong><em>ệ</em></strong><strong><em>p t</em></strong><strong><em>ừ</em></strong><strong><em> tr</em></strong><strong><em>ườ</em></strong><strong><em>ng Ritsumeikan Asia pacific University (APU)</em></strong><strong><em>.</em></strong></p>\n<p>A- Phi&ecirc;n dịch cho bộ phận Marketing:</p>\n<p>C&oacute; kiến thức về kinh doanh, marketing.</p>\n<p>B- Phi&ecirc;n dịch cho bộ phận Service:</p>\n<p>C&oacute; kiến thức về &ocirc; t&ocirc;.</p>\n<p>&diams; &nbsp;Hồ sơ bao gồm:</p>\n<p>+ &nbsp; Đơn xin việc.</p>\n<p>+ &nbsp; Sơ yếu l&yacute; lịch.</p>\n<p>+ &nbsp; Bản sao c&aacute;c bằng cấ