INSERT INTO `pages` VALUES(15, 16, 3, 'Bảo hành', 'bao-hanh', '<p><strong>+ Thời hạn bảo h&agrave;nh đối với b&igrave;nh điện v&agrave; c&ograve;i xe</strong></p>\n<p>B&igrave;nh điện v&agrave; c&ograve;i do ch&iacute;nh IVC lắp đặt tr&ecirc;n c&aacute;c xe Isuzu sẽ được bảo h&agrave;nh&nbsp;<strong>06 th&aacute;ng</strong>&nbsp;hoặc&nbsp;<strong>10.000km</strong>&nbsp;t&ugrave;y theo điều kiện n&agrave;o đến trước, t&iacute;nh từ ng&agrave;y giao xe cho người mua để sử dụng đầu ti&ecirc;n, hoặc ng&agrave;y đầu ti&ecirc;n m&agrave; chiếc xe được đưa v&agrave;o sử dụng, t&ugrave;y theo ng&agrave;y n&agrave;o đến trước.</p>\n<p><strong>+ Dịch vụ sửa chữa lưu động Isuzu</strong></p>\n<p>Để n&acirc;ng cao hơn nữa chất lượng dịch vụ &amp; chăm s&oacute;c kh&aacute;ch h&agrave;ng c&ocirc;ng ty &Ocirc;t&ocirc; Isuzu Việt Nam cũng đ&atilde; triển khai&nbsp;<strong>Dịch vụ sừa chữa lưu động</strong>&nbsp;(Mobile service) tại hệ thống c&aacute;c đại l&yacute; Isuzu Việt Nam tr&ecirc;n to&agrave;n quốc để c&oacute; thể đ&aacute;p ứng nhu cầu dịch vụ của kh&aacute;ch h&agrave;ng mọi l&uacute;c&nbsp; mọi nơi. Với khẩu hiệu &ldquo;Isuzu lu&ocirc;n lu&ocirc;n đồng h&agrave;nh c&ugrave;ng bạn&rdquo;, c&ocirc;ng ty &Ocirc;t&ocirc; Isuzu Việt Nam mong muốn sẽ lu&ocirc;n đồng h&agrave;nh c&ugrave;ng những th&agrave;nh c&ocirc;ng của kh&aacute;ch h&agrave;ng.</p>\n<p><img src="http://bsc.heteml.jp/isuzu/wp-content/uploads/2010/10/208241637571.gif" alt="" /></p>', '1', 1375128105, 1375128105, 9, '1');
INSERT INTO `pages` VALUES(16, 13, 2, 'THÙNG XE TẢI CÁNH ĐÔI', 'thung-xe-tai-canh-doi', '<p>&nbsp; &nbsp; &nbsp;</p>\n<div style="width: 580px;">\n<div>\n<div style="font-weight: bold;">Xe tải th&ugrave;ng c&aacute;nh dơi, ph&ugrave; hợp với loại h&agrave;ng h&oacute;a nguy&ecirc;n kiện k&iacute;ch thước, khối lượng lớn.</div>\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;\n<div>\n<ul>\n<li>Th&ugrave;ng xe thiết kế với 02 c&aacute;nh dơi điều khiển bằng hệ thống thủy lực, dễ d&agrave;ng chất dỡ h&agrave;ng h&oacute;a &nbsp; c&oacute; k&iacute;ch thước, khối lượng lớn cồng kềnh bằng xe n&acirc;ng. &nbsp;</li>\n&nbsp;\n<li>Xe c&oacute; thể d&ugrave;ng l&agrave;m s&acirc;n khấu di động, cửa h&agrave;ng di động, giới thiệu sản phẩm mới...</li>\n&nbsp;\n<li>Vật liệu th&ugrave;ng xe đa dạng, nhiều sự lựa chọn: &nbsp; &nbsp; &nbsp; &nbsp;\n<ul>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n<li>V&aacute;ch ngo&agrave;i: Composite, t&ocirc;n, nh&ocirc;m, inox. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</li>\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n<li>S&agrave;n th&ugrave;ng: Gỗ hoặc t&ocirc;n phẳng.</li>\n&nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;</ul>\n</li>\n</ul>\n</div>\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n<div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <img style="margin-left: auto; margin-right: auto;" src="http://www.uphinhnhanh.com/images/17Isuzu_Forward.png" width="500px" /> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <br /> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <img src="http://www.uphinhnhanh.com/images/46Isuzu_Forward2.png" width="500px" /></div>\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</div>\n&nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n<div style="width: 100%;">&nbsp; &nbsp; &nbsp; &nbsp; <br /> &nbsp; &nbsp; &nbsp; &nbsp;\n<div>TH&Ugrave;NG TẢI C&Aacute;NH DƠI / FULL WING</div>\n&nbsp;&nbsp;\n<table style="font-size: 10px; font-family: Arial; border-top: 1px solid #696868; border-right: 1px solid #696868; b