INSERT INTO `pages` VALUES(22, 18, 3, 'Vị trí số khung - số máy xe N-SERIES và F-SERIES', 'vi-tri-so-khung-so-may-xe-n-series-va-f-series', '<p style="text-align: center;"><strong>&nbsp;</strong></p>\n<p style="text-align: left;"><strong>&nbsp; &nbsp; &nbsp; 1.Vị tr&iacute; số khung</strong></p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Được đ&oacute;ng l&ecirc;n khung gầm b&ecirc;n phải ph&iacute;a trước gần b&aacute;nh xe trước.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Số m&aacute;y được đ&oacute;ng ch&igrave;m v&agrave;o th&acirc;n chassis v&agrave; c&oacute; thể quan s&aacute;t dễ d&agrave;ng nếu n&acirc;ng cabin xe &amp; đứng b&ecirc;n ph&iacute;a phụ xế.</p>\n<p><img src="http://www.uphinhnhanh.com/images/97sokhungsomay1.jpg" alt="" width="419" height="303" style="display: block; margin-left: auto; margin-right: auto;" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2.<strong>Vị tr&iacute; số m&aacute;y /động cơ</strong></p>\n<p>&nbsp; &nbsp;+&nbsp;<strong>Động cơ 4HF1</strong></p>\n<p>Model xe &nbsp;Isuzu: <strong>NKR66, NHR</strong></p>\n<p>Vị tr&iacute; số m&aacute;y : B&ecirc;n phụ xế - ph&iacute;a dưới ch&acirc;n m&aacute;y. Từ chữ số 4 trong chữ 4HF1, đi xuống 5 cm. Số m&aacute;y gồm 6 chữ số, đ&oacute;ng ch&igrave;m.</p>\n<p><img src="http://www.uphinhnhanh.com/images/72sokhungsomay2.jpg" alt="" style="display: block; margin-left: auto; margin-right: auto;" /></p>\n<p style="text-align: center;"><img src="http://www.uphinhnhanh.com/images/94sokhungsomay3.jpg" alt="" /></p>\n<p>&nbsp; + <strong>Động cơ 4JJ1- động cơ xe tải</strong></p>\n<p>Model xe&nbsp; Isuzu: <strong>NMR85 , NPR85</strong></p>\n<p>Vị tr&iacute; số m&aacute;y : B&ecirc;n t&agrave;i xế - ph&iacute;a tr&ecirc;n, gần lọc nhi&ecirc;n liệu. Ph&iacute;a tr&ecirc;n chữ 4JJ1. Số m&aacute;y gồm 6 chữ số, đ&oacute;ng ch&igrave;m.</p>\n<p><img src="http://www.uphinhnhanh.com/images/56sokhungsomay4.jpg" alt="" style="display: block; margin-left: auto; margin-right: auto;" /></p>\n<p><img src="http://www.uphinhnhanh.com/images/18sokhungsomay5.jpg" alt="" style="display: block; margin-left: auto; margin-right: auto;" />&nbsp; + <strong>Động cơ 4HK1- động cơ xe tải</strong></p>\n<p>Model xe : <strong>NQR75, FRR</strong></p>\n<p>Vị tr&iacute; số m&aacute;y : B&ecirc;n phụ xế - ph&iacute;a dưới nằm ẩn b&ecirc;n trong ch&acirc;n m&aacute;y. Từ chữ số 4 trong chữ 4HK1 tr&ecirc;n block m&aacute;y, đi xuống 5 cm. Số m&aacute;y gồm 6 chữ số, đ&oacute;ng ch&igrave;m.</p>\n<p><img src="http://www.uphinhnhanh.com/images/82sokhungsomay6.jpg" alt="" style="display: block; margin-left: auto; margin-right: auto;" /></p>\n<p style="text-align: center;"><img src="http://www.uphinhnhanh.com/images/91sokhungsomay7.jpg" alt="" /></p>\n<p>&nbsp; + <strong>Động cơ 4JB1- động cơ</strong></p>\n<p>Model xe : <strong>NKR55, NLR55, QKR55</strong></p>\n<p>Vị tr&iacute; số m&aacute;y : B&ecirc;n t&agrave;i xế - ph&iacute;a tr&ecirc;n m&aacute;y khởi động ; s&aacute;t ph&iacute;a đầu tr&acirc;u. Số m&aacute;y gồm 6 chữ số, đ&oacute;ng ch&igrave;m.</p>\n<p><img src="http://www.uphinhnhanh.com/images/24sokhungsomay_9.jpg" alt="" style="display: block; margin-left: auto; margin-right: auto;" /></p>\n<p><img src="http://www.uphinhnhanh.com/images/74sokhungsomay10.jpg" alt="" style="display: block; margin-left: auto; margin-right: auto;" /></p>\n<p>&nbsp; + <strong>Động cơ 6HK1</strong></p>\n<p>Model xe : <strong>FVR, FVM</strong></p>\n<p>Vị tr&iacute; số m&aacute;y : B&ecirc;n phụ xế - ph&iacute;a tr&ecirc;n gần m&aacute;y ph&aacute;t ; Số m&aacute;y gồm 6 chữ số, đ&oacute;ng ch&igrave;m. Trước số m&aacute;y c&oacute; model động cơ 6HK1 đ&oacute;ng ch&igrave;m</p>\n<p style="text-align: center;"><img src="http://www.uphinhnhanh.com/images/40sokhungsomay11.jpg" alt="" /></p>\n<p></p>', '1', 1375250765, 1375250765, 9, '1');
INSERT INTO `pages` VALUES(23, 18, 3, 'VỊ TRÍ SỐ  KHUNG – SỐ MÁY TRÊN MODEL XE D-MAX', 'vi-tri-so-khung-so-may-tren-model-xe-d-max', '<p><strong></strong><strong>&nbsp; &nbsp; <span style="color: #ffffff;">&nbsp;</span><span style="color: #ffffff;">1.Vị tr&iacute; số khung xe D-Max</span></strong></p>\n<p><span style="color: #ffffff;">Số khung xe được đ&oacute;ng ch&igrave;m v&agrave;o th&acirc;n chassis xe, ở vị tr&iacute; giữa cửa trước v&agrave; cửa saiu, b&ecirc;n ph&iacute;a phụ xế.</span></p>\n<p style="text-align: center;"><img src="http://www.uphinhnhanh.com/images/89a5.png" alt="" /></p>\n<p style="text-align: left;"><strong>&nbsp; &nbsp;<span style="color: #ffffff;"> 2. Vị tr&iacute; số m&aacute;y/động cơ</span></strong></p>\n<p><span style="color: #ffffff;">&nbsp; &nbsp; &nbsp; &nbsp;+ <strong>Động cơ 4JA1 &ndash; 4JH1</strong></span></p>\n<p><span style="color: #ffffff;">Model xe Isuzzu &nbsp;: <strong>D-Max sản xuất trước năm 2008</strong></span></p>\n<p><span style="color: #ffffff;">Vị tr&iacute; số m&aacute;y : B&ecirc;n ph&iacute;a t&agrave;i xế - ph&iacute;a tr&ecirc;n m&aacute;y khởi động ; s&aacute;t ph&iacute;a đầu tr&acirc;u. Số m&aacute;y gồm 6 chữ số, đ&oacute;ng ch&igrave;m.</span></p>\n<p><img src="http://www.uphinhnhanh.com/images/92vitrisokhungsomay1.jpg" alt="" style="display: block; margin-left: auto; margin-right: auto;" /></p>\n<p style="text-align: center;"><img src="http://www.uphinhnhanh.com/images/22vitrisokhungsomay2.jpg" alt="" /></p>\n<p>&nbsp; &nbsp;<strong> &nbsp;<span style="color: #ffffff;">+ Động cơ 4JJ1</span></strong></p>\n<p><span style="color: #ffffff;">Model xe Isuzu: <strong>Xe D-max &nbsp;sản xuất từ năm 2008, trang bị động cơ common rail</strong></span></p>\n<p><span style="color: #ffffff;">Vị tr&iacute; số m&aacute;y : <strong>B&ecirc;n phụ xế - ph&iacute;a dưới ; s&aacute;t ph&iacute;a đầu tr&acirc;u. Số m&aacute;y gồm 6 chữ số, đ&oacute;ng ch&igrave;m.</strong></span></p>\n<p><img src="http://www.uphinhnhanh.com/images/80vitrisokhungsomay3.jpg" alt="" style="display: block; margin-left: auto; margin-right: auto;" /></p>\n<p><img src="http://www.uphinhnhanh.com/images/96vitrisokhungsomay4.jpg" alt="" style="display: block; margin-left: auto; margin-right: auto;" /></p>\n<p><img src="http://www.uphinhnhanh.com/images/50vitrisokhungsomay5.jpg" alt="" style="display: block; margin-left: auto; margin-right: auto;" /></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>', '1', 1375251813, 1375251813, 9, '1');
INSERT INTO `pages` VALUES(24, 21, 3, 'Kiến thức ôtô - xe tải', 'kien-thuc-oto-xe-tai', '<p>+ KHẢ NĂNG VƯỢT DỐC TỐI ĐA CỦA XE</p>\n<p>+ &Yacute; NGHĨA C&Aacute;C K&Iacute; HIỆU VIẾT TẮT THƯỜNG GẶP TR&Ecirc;N XE &Ocirc;T&Ocirc;</p>', '1', 1375254152, 1375254152, 9, '1');
INSERT INTO `pages` VALUES(25, 21, 3, 'KHẢ NĂNG VƯỢT DỐC TỐI ĐA CỦA XE', 'kha-nang-vuot-doc-toi-da-cua-xe', '<p>Một trong c&aacute;c th&ocirc;ng số m&agrave; c&aacute;c nh&agrave; sản xuất xe thường c&ocirc;ng bố &amp; được mọi người quan t&acirc;m ch&iacute;nh l&agrave; khả năng vượt dốc tối đa của xe. Th&ocirc;ng số n&agrave;y được t&iacute;nh to&aacute;n dựa tr&ecirc;n c&aacute;c th&ocirc;ng số c&ocirc;ng suất, moment xoắn cực đại của động cơ, tự trọng của xe&hellip;.. <br />Th&ocirc;ng thường khả năng vượt dốc c&oacute; th&ocirc;ng số l&agrave; %. <br />Dựa tr&ecirc;n c&aacute;c th&ocirc;ng số n&agrave;y người sử dụng c&oacute; thể biết được khả năng xe của m&igrave;nh vượt được dốc cao tối đa bao nhi&ecirc;u. <br />Dựa v&agrave;o đồ thị đ&iacute;nh k&egrave;m, ch&uacute;ng ta c&oacute; thể thấy được độ dốc (g&oacute;c) tương ứng với từng phần trăm (%) của khả năng vượt dốc.</p>\n<p><img src="http://www.uphinhnhanh.com/images/39kha_nang_vuot_doc.jpg" alt="" style="display: block; margin-left: auto; margin-right: auto;" /></p>', '1', 1375254406, 1375254406, 9, '1');
INSERT INTO `pages` VALUES(26, 21, 3, 'Ý NGHĨA CÁC KÍ HIỆU VIẾT TẮT THƯỜNG GẶP TRÊN XE ÔTÔ', 'y-nghia-cac-ki-hieu-viet-tat-thuong-gap-tren-xe-oto', '<p>4X2 (2 wheel drive) : Dẫn động 2 b&aacute;nh <br /> 4WD, 4x4 (4 wheel drive) Dẫn động 4 b&aacute;nh <br /> ABS (anti-lock brake system) Hệ thống chống b&oacute; cứng phanh. <br /> API (American Petroleum Institute) : Hiệp hội dầu kh&iacute; Hoa K&igrave;. Thường gặp ở bảng ti&ecirc;u chuẩn cấp độ nhớt <br /> BA (brake assist) Hệ thống hỗ trợ phanh gấp. <br /> Common Rail : Hệ thống phun dầu điện tử <br /> DOHC (double overhead camshafts) 2 trục cam ph&iacute;a tr&ecirc;n xi-lanh. <br /> DRM (data record module) : Thiết bị ghi nhớ hoạt động của xe <br /> EBD (Electronic Brake-force Distribution) : hệ thống ph&acirc;n phối lực phanh điện tử <br /> ESC ( Electronic Stability Control) : Hệ thống c&acirc;n bằng điện tử <br /> ECU (electronic control unit) : Hộp điều khiển hoạt động của xe <br /> ECM (electronic control modul): Hộp điều khiển hoạt động của động cơ xe Isuzu <br /> EDC (electronic damper control) Hệ thống điều chỉnh giảm &acirc;m điện tử. <br /> EFI (electronic fuel Injection) Hệ thống phun xăng điện tử. <br />EGR (Exhaust Gas Recirculation) : Van lu&acirc;n hồi kh&iacute; thải <br /> ESC ( Electronic Stability Control) : Hệ thống c&acirc;n bằng điện tử <br /> ESP (electronic stability program) Hệ thống tự động c&acirc;n bằng điện tử. <br /> GVW (Gross vehicle weight) : Trọng lượng to&agrave;n bộ xe <br /> Hard-top Kiểu xe mui kim loại cứng kh&ocirc;ng c&oacute; khung đứng giữa 2 cửa trước v&agrave; sau. <br /> Hybrid Kiểu xe c&oacute; phần động lực được thiết kế kết hợp từ 2 dạng m&aacute;y trở l&ecirc;n. V&iacute; dụ: xe &ocirc;t&ocirc; xăng-điện, xe đạp m&aacute;y... <br /> I4 , I6 Dạng động cơ gồm 4 hoặc 6 xi-lanh xếp th&agrave;nh 1 h&agrave;ng thẳng. <br /> IOE (intake over exhaust) Van nạp nằm ph&iacute;a tr&ecirc;n van xả. <br /> I-Mark salon : Hệ thống đại l&yacute; Isuzu đạt ti&ecirc;u chuẩn hoạt động theo quy định của Isuzu to&agrave;n cầu <br /> IVC (Isuzu Vietnam Company) : C&ocirc;ng ty TNHH &ocirc; t&ocirc; Isuzu Việt Nam <br /> IGEO (Isuzu Genuine Engine Oil) : Nhớt động cơ ch&iacute;nh h&atilde;ng Isuzu <br />JASO (Japanese Automobile Standards Organization) : Tổ chức chứng nhận &ocirc;t&ocirc; Nhật Bản <br /> MID (multi information display) : M&agrave;n h&igrave;nh hiển thị đa th&ocirc;ng tin <br /> OHV (overhead valves) Trục cam nằm dưới v&agrave; t&aacute;c động v&agrave;o van qua c&aacute;c tay đ&ograve;n. <br /> Pikup Kiểu xe hơi 4 chỗ c&oacute; th&ugrave;ng chở h&agrave;ng rời ph&iacute;a sau ca-bin (xe b&aacute;n tải) <br /> Sedan Loại xe h&ograve;m k&iacute;nh 4 cửa, ca-p&ocirc; v&agrave; khoang h&agrave;nh l&yacute; thấp hơn ca-bin. <br /> SOHC (single overhead camshafts) Trục cam đơn tr&ecirc;n đầu xi-lanh. <br /> SUV (sport utility vehicle) Kiểu xe thể thao đa chức năng, 4x2 hoặc 4x4 vượt những địa h&igrave;nh xấu. <br /> SAE (Society of Automotive Engineers) : Hiệp hội kỹ sư tự động h&oacute;a. Thường gặp ở bảng ti&ecirc;u chuẩn cấp độ nhớt <br /> TC (Turbo charger) Turbo thiết kế tăng &aacute;p của động cơ. <br /> V6 , V8 Dạng động cơ gồm 6 hoặc 8 xi-lanh, xếp th&agrave;nh 2 h&agrave;ng nghi&ecirc;ng, mặt cắt cụm m&aacute;y h&igrave;nh chữ V. <br /> VIN (Vehicle Identification Number) : Số khung xe <br /> VSC (vehicle skid control) Hệ thống kiểm so&aacute;t t&igrave;nh trạng trượt b&aacute;nh xe. <br /> VGS Turbo (Variable Geometry System) : Turbo c&oacute; khả năng thay đổi gi&aacute; trị đ&oacute;ng mở c&aacute;c c&aacute;nh quạt b&ecirc;n trong </p>', '1', 1375254640, 1375254640, 9, '1');
INSERT INTO `pages` VALUES(27, 21, 3, 'test', 'test', '<p style="text-align: center;"><strong>&nbsp;</strong></p>\n<p style="text-align: left;"><strong>&nbsp; &nbsp; &nbsp; 1.Vị tr&iacute; số khung</strong></p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Được đ&oacute;ng l&ecirc;n khung gầm b&ecirc;n phải ph&iacute;a trước gần b&aacute;nh xe trước.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Số m&aacute;y được đ&oacute;ng ch&igrave;m v&agrave;o th&acirc;n chassis v&agrave; c&oacute; thể quan s&aacute;t dễ d&agrave;ng nếu n&acirc;ng cabin xe &amp; đứng b&ecirc;n ph&iacute;a phụ xế.</p>\n<p><img src="http://www.uphinhnhanh.com/images/97sokhungsomay1.jpg" alt="" width="419" height="303" style="display: block; margin-left: auto; margin-right: auto;" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2.<strong>Vị tr&iacute; số m&aacute;y /động cơ</strong></p>\n<p>&nbsp; &nbsp;+&nbsp;<strong>Động cơ 4HF1</strong></p>\n<p>Model xe &nbsp;Isuzu: <strong>NKR66, NHR</strong></p>\n<p>Vị tr&iacute; số m&aacute;y : B&ecirc;n phụ xế - ph&iacute;a dưới ch&acirc;n m&aacute;y. Từ chữ số 4 trong chữ 4HF1, đi xuống 5 cm. Số m&aacute;y gồm 6 chữ số, đ&oacute;ng ch&igrave;m.</p>\n<p><img src="http://www.uphinhnhanh.com/images/72sokhungsomay2.jpg" alt="" style="display: block; margin-left: auto; margin-right: auto;" /></p>\n<p style="text-align: center;"><img src="http://www.uphinhnhanh.com/images/94sokhungsomay3.jpg" alt="" /></p>\n<p>&nbsp; + <strong>Động cơ 4JJ1- động cơ xe tải</strong></p>\n<p>Model xe&nbsp; Isuzu: <strong>NMR85 , NPR85</strong></p>\n<p>Vị tr&iacute; số m&aacute;y : B&ecirc;n t&agrave;i xế - ph&iacute;a tr&ecirc;n, gần lọc nhi&ecirc;n liệu. Ph&iacute;a tr&ecirc;n chữ 4JJ1. Số m&aacute;y gồm 6 chữ số, đ&oacute;ng ch&igrave;m.</p>\n<p><img src="http://www.uphinhnhanh.com/images/56sokhungsomay4.jpg" alt="" style="display: block; margin-left: auto; margin-right: auto;" /></p>\n<p><img src="http://www.uphinhnhanh.com/images/18sokhungsomay5.jpg" alt=""