INSERT INTO `products` VALUES(29, 1, 'D-MAX LS 4x4 MT', 'd-max-ls-4x4-mt', '<p>D-MAX LS 4x4 MT</p>', '<p>ISUZU D-Max 2011 NHẬP KHẨU NGUY&Ecirc;N CHIẾC&nbsp;<br /><br />ISUZU D-MAX 2011 l&agrave; chiếc xe được thiết kế để chinh phục mọi địa h&igrave;nh kh&oacute; khăn nhất. C&oacute; d&aacute;ng vẻ chắc chắn, bền bỉ với thiết kế khung gầm thừa hưởng từ khung gầm xe tải ISUZU vốn đ&atilde; rất nổi tiếng qua nhiều thập kỷ qua., xe vận h&agrave;nh ho&agrave;n to&agrave;n &ecirc;m &aacute;i tr&ecirc;n đường trường v&agrave; ổn định tr&ecirc;n đường xa lộ nhờ hệ số k&eacute;o thấp của thiết kế kh&iacute; động học.&nbsp;<br /><br />D-MAX được nhập khẩu nguy&ecirc;n chiếc với 6 mầu để qu&yacute; kh&aacute;ch h&agrave;ng lựa chọn bao gồm xanh hải qu&acirc;n, bạc, trắng, x&aacute;m, đen, đỏ c&oacute; gi&aacute; c&ocirc;ng bố từ 606,100,000 đ&atilde; bao gồm thuế VAT.</p>', NULL, 'http://isuzu-vietnam.com/demo/uploads/assets/media/products/D-Max_LS_4x4_MT-ok.png', '1:37|2:64|3:65|4:66|5:67', 1375213735, 1375213735, '0', 9, '1');
